import { Component, Input, Output, EventEmitter } from '@angular/core';

import { Product } from '../../models/product.model';

@Component({
  selector: 'app-product',
  templateUrl: './product.component.html',
  styleUrls: ['./product.component.scss']
})
export class ProductComponent {

  @Input() product: Product = {
    id: '',
    price: 0,
    images: [],
    title: '',
    category: {
      id: -1,
      name: '',
    },
    description: ''
  };
  @Output() addedProduct = new EventEmitter<Product>();
  @Output() showProduct = new EventEmitter<string>();

  constructor() { }

  onAddToCart() {
    this.addedProduct.emit(this.product);
  }

  onShowDetail() {
    this.showProduct.emit(this.product.id);
  }

}
<a [routerLink]="['/product', product.id]"></a>
<app-img *ngIf="product.images.length > 0" [img]="product.images[0]"></app-img>
<h2 class="title">{{ product.price | currency }}</h2>
<p>{{ product.title }}</p>
<p class="category">{{ product.category?.name }}</p>
<button (click)="onShowDetail()">Ver detalle</button>
<button (click)="onAddToCart()">Add cart</button>
